import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css"; 
import "slick-carousel/slick/slick-theme.css";
import "./imageCarousel.css";
import { Fade } from "react-reveal"

const state = {
    responsive : [
          {
            breakpoint: 3000,
            settings: {
              slidesToShow: 1,
              centerPadding: "30%"
            }
          },
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 1,
              centerPadding: "30%"
            }
          },
          {
            breakpoint: 780,
            settings: {
              slidesToShow: 1,
              centerPadding: "10%",
            }
          },
          {
            breakpoint: 480,
            settings: {
              arrows: false,
              slidesToShow: 1,
              centerPadding: "15%",
            }
          }]
    }
    var settings = {
        className: "center",
        centerMode: true,
        infinite: true,
        responsive: state.responsive,
        dots: true,
        swipeToSlide: true,
        focusOnSelect: true
    };

export function Awards () {        
    return (
        <section className="section" id="awards">
        <div>
        <Fade bottom duration={1000} distance="20px" delay={1500}>
            <div className="carousel-wrapper">
            <Slider {...settings} className="carousel-main">
                <img src={require("../media/introImg1.jpg")} className="carousel-image" alt="1"/>
                <img src={require("../media/introImg3.jpg")} className="carousel-image" alt="2"/>
                <img src={require("../media/introImg2.jpg")} className="carousel-image" alt="3"/>
                <img src={require("../media/introImg4.jpg")} className="carousel-image" alt="4"/>
            </Slider>
            </div>
        </Fade>
        </div>
        </section>
    )
}
