function Skills() {
  return (
    <section className="section" id="skills">
      <div className="container">
        <h1 className="title">Skills</h1>
          <div className="has-text-centered">
            <span className="icon has-text-link">
              <i className="fas fa-3x fa-laptop-code"></i>
            </span>
            <h2 className="title is-5">Software Development</h2>
          </div>
          <div className="field is-grouped is-grouped-multiline is-grouped-centered has-text-centered">
            {Resume.skills.map((value, index, key) => {
              return (
                <div class="card">
                <header class="card-header">
                  <p class="card-header-title">
                    {value}
                  </p>
                  <button class="card-header-icon" aria-label="more options">
                    <span class="icon">
                      <i class="fas fa-angle-down" aria-hidden="true"></i>
                    </span>
                  </button>
                </header>
              </div>
              );
            })}
          </div>
        </div>
    </section>
  );
}

export default Skills;
